    /**
     * @return mixed
     */
    public static function getRandText() {
        $count = count(self::content);
        return self::content[wp_rand(0, $count -1)];
    }

    /**
     * @return mixed
     */
    public static function getRandParagraph() {
        return '<p>'. self::getRandText() .'</p>';
    }

    /**
     * @param int $paragraphs
     * @return string
     */
    public static function getParagraphs($paragraphs = 2) {

        $content = '';

        foreach(self::content AS $k => $paragraph)
        {
            if($k === $paragraphs)
            {
                break;
            }

            $content .= '<p>' . $paragraph .'</p>';
        }

        if($paragraphs > count(self::content))
        {
            for($i = $paragraphs - count(self::content); $i > 0; $i--)
            {
                $content .= '<p>' . self::getRandText() .'</p>';
            }
        }

        return $content;
    }

